INSERT INTO location (name, description, retired, uuid, date_created, creator) VALUES ('Hospital Visit', 'Hospital Visit Parent location', FALSE, uuid(), NOW(), 1);

SELECT location_tag_id INTO @location_tag_id FROM location_tag WHERE name = 'Visit Location';
SELECT location_id INTO @location_id1 FROM location WHERE location.name='Hospital Visit';

INSERT INTO location_tag_map(location_id, location_tag_id) VALUES(@location_id1, @location_tag_id);

SELECT location_id INTO @location_id1 FROM location WHERE location.name='Hospital Visit';
update location set parent_location = @location_id1 where `name` = 'IPD';
update location set parent_location = @location_id1 where `name` = 'OPD';
update location set parent_location = @location_id1 where `name` = 'MLO';

select concept_id into @concept_id1 from concept_name where name = 'PA, Left : Radial Dev.';
select concept_id into @concept_id2 from concept_name where name = 'PA,MTUL Left : Radial Dev.';
select concept_id into @concept_id3 from concept_name where name = 'PA, Right : Radial Dev.';
select concept_id into @concept_id4 from concept_name where name = 'PA,MTUL Right : Radial Dev.';

update concept_name set name = 'ROM, Left : Radial Dev.' where concept_id = @concept_id1 and concept_name_type = 'SHORT';
update concept_name set name = 'MTUL, Left : Radial Dev.' where concept_id = @concept_id2 and concept_name_type = 'SHORT';
update concept_name set name = 'ROM, Right : Radial Dev.' where concept_id = @concept_id3 and concept_name_type = 'SHORT';
update concept_name set name = 'MTUL Right : Radial Dev.' where concept_id = @concept_id4 and concept_name_type = 'SHORT';
